mapper.sql:
select * from plateform_merchan_apply t

    and t.id = #{id}
    and t.pm_name like '%${pmName}%'
    and t.confirm_type = #{confirmType}
    and t.apply_phone = #{applyPhone}
    and t.apply_email = #{applyEmail}
    and t.pm_company_person = #{pmCompanyPerson}
    and t.apply_ways = #{applyWays}
    and t.applyer_name like '%${applyerName}%'
    and t.apply_content = #{applyContent}
    and t.apply_memo = #{applyMemo}
    and t.create_time = #{createTime}
    and t.apply_state = #{applyState}
    and t.sm_send_state = #{smSendState}
    and t.sm_random_code = #{smRandomCode}
    and t.sm_send_time = #{smSendTime}
    and t.sm_confirm_state = #{smConfirmState}
    and t.email_send_state = #{emailSendState}
    and t.email_random_code = #{emailRandomCode}
    and t.email_send_time = #{emailSendTime}
    and t.email_confirm_state = #{emailConfirmState}
    and t.ope_code = #{opeCode}
    and t.ope_name = #{opeName}
    and t.lock_time = #{lockTime}
    and t.checker_code = #{checkerCode}
    and t.checker_name = #{checkerName}
    and t.check_time = #{checkTime}
    and t.create_time >= #{createTimeStart}
    and t.create_time <= #{createTimeEnd}
